import React, { useState, useEffect } from "react";
import TodoList from "./components/TodoList";
import axios from "axios";
import "./styles.css";

export default function App() {
  const [todos, setTodos] = useState([]);
  const [activeTab, setActiveTab] = useState("todo");

  useEffect(() => {
    axios.get("http://localhost:9091/api/todo").then(({ data }) => {
      setTodos(data);
    });
  }, []);

  const toggleCompleted = async (id) => {
    const updated = await axios.patch(`http://localhost:9091/api/todo/${id}/completed`);
    setTodos((prev) => prev.map((todo) => (todo.id === id ? updated.data : todo)));
  };

  const todosActive = todos.filter((t) => !t.completed);
  const todosDone = todos.filter((t) => t.completed);

  return (
    <div className="todo-app">
      <h1>Todo List</h1>
      <div className="tabs">
        <button onClick={() => setActiveTab("todo")} className={activeTab === "todo" ? "active" : ""}>
          To Do
        </button>
        <button onClick={() => setActiveTab("done")} className={activeTab === "done" ? "active" : ""}>
          Completed
        </button>
      </div>

      {activeTab === "todo" && (
        <TodoList todos={todosActive} toggleCompleted={toggleCompleted} />
      )}
      {activeTab === "done" && (
        <TodoList todos={todosDone} toggleCompleted={toggleCompleted} />
      )}
    </div>
  );
}
